import React, { Component } from "react";
import { Button, Form, Alert, Container, Row, Col } from "reactstrap";
import withAuthorization from "./withAuthorization"; //redirects to sign in if user not signed in
import { auth } from "../firebase";
import { db } from "../firebase";
import { storage } from "../firebase/auth";
import Navigation from "./Navigation/Navigation";

const byPropKey = (propertyName, value) => () => ({
  [propertyName]: value,
});

const INITIAL_STATE = {
  email: "",
  error: null,
  showingAlert: false,
  users: null,
  username: "",
  loading: true,
  first_name: "",
  last_name: "",
  grade: "",
  update: 0,
  gre: "",
  we: "",
  toefl: "",
  resume: "",
  sop: "",
  entry: "",
  uid: "",
  redirect: null,
  lor1: "",
  lor2: "",
  lor3: "",
  lor4: "",
  progresssop: "",
  progressresume: "",
  url_resume: "",
  url_sop: "",
};

class AccountPage extends Component {
  componentDidMount() {
    const { loggedUser } = this.props;
    console.log(loggedUser.uid);
    if (loggedUser) {
      db.doGetAnUnser(loggedUser.uid).then((res) => {
        this.setState({
          username: res.val().username,
          email: res.val().email,
          loading: false,
          uid: loggedUser.uid,
        });
        console.log(res.val());
      });
    }
    db.doGetForm(loggedUser.uid).then((res) => {
      this.setState({
        first_name: res.val().first_name,
