import React, { Component } from "react";
import { Button, Form, Alert } from "reactstrap";
import withAuthorization from "./withAuthorization"; //redirects to sign in if user not signed in
import { auth } from "../firebase";
import { db } from "../firebase";

const byPropKey = (propertyName, value) => () => ({
  [propertyName]: value
});

const INITIAL_STATE = {
  email: "",
  error: null,
  showingAlert: false,
  users: null,
  username: '',
  loading: true,
  first_name: '',
  last_name: '',
  grade: '',
  update: 0,
  gre: '',
  we: '',
  toefl:'',
  resume: '',
  sop: '',
  entry: '',
  uid: '',
  redirect: null,
  lor1: '',
  lor2: '',
  lor3: '',
  lor4: '',
  progresssop: '',
  progressresume: '',
  url_resume: '',
  url_sop: ''
};

class AccountPage extends Component {
  componentDidMount() {

    const { loggedUser } = this.props;
    console.log(loggedUser.uid)
    if(loggedUser)
    {
    db.doGetAnUnser(loggedUser.uid).then(res => {
      this.setState({
        username: res.val().username,
        email: res.val().email,
        loading: false,
        uid: loggedUser.uid
      });
      console.log(res.val())

    });
    }
    db.doGetForm(loggedUser.uid).then(res => {
      this.setState({
        first_name: res.val().first_name,
        last_name: res.val().last_name,
        grade: res.val().grade,
        gre: res.val().gre,
        we: res.val().work_ex,
        toefl: res.val().toefl,
        lor1: res.val().lor1,
        lor2: res.val().lor2,
        lor3: res.val().lor3,
        lor4: res.val().lor4
      });
      console.log(res.val())
    });
  }

state = { ...INITIAL_STATE };

  onSubmit = event => {
    const email = this.state.email;

    auth
      .doPasswordReset(email)
      .then(() => {
        this.setState({ ...INITIAL_STATE });
      })
      .catch(error => {
        this.setState(byPropKey("error", error));
        this.timer(); //show alert message for some seconds
      });
    event.preventDefault();
    setTimeout(function () {
      window.location.reload(1);
  }, 3000);  
  };

  timer = () => {
    this.setState({
      showingAlert: true
    });

    setTimeout(() => {
      this.setState({
        showingAlert: false
      });
    }, 4000);

  };

  render() {
    const { error, showingAlert } = this.state;

    return (
      <div>
        {showingAlert && (
          <Alert color="danger" onLoad={this.timer}>
            {error.message}
          </Alert>
        )}
        <br></br>
        <h5>Reset Link will be sent to your Email</h5>
        <br></br>
        <Form onSubmit={this.onSubmit}>
          <div className="text-center">
            <Button type="submit">
              Reset My Password
            </Button>
          </div>
        </Form>
          <form >
            <br></br>
            <h1 className="h3 mb-3 font-weight-normal">Your Form Data</h1>
            <div className="form-group">
              <label htmlFor="name">First name</label>
              <input
                type="text"
                className="form-control"
                name="first_name"
                disabled= {true}
                value={this.state.first_name}
              />
            </div>
            <div className="form-group">
              <label htmlFor="name">Last name</label>
              <input
                type="text"
                className="form-control"
                name="last_name"
                value={this.state.last_name}
              />
            </div>
            <div className="form-group">
              <label htmlFor="grade">College CGPA</label>
              <input
                type="number" 
                className="form-control"
                pattern="[0-9]*"
                name="grade"
                value={this.state.grade}
              />
            </div>
            <div className="form-group">
              <label htmlFor="gre">GRE</label>
              <input
                type="number" 
                className="form-control"
                pattern="[0-9]*"
                name="gre"
                value={this.state.gre}
              />
            </div>
            <div className="form-group">
              <label htmlFor="we">Work Experience (Months)</label>
              <input
                type="number" 
                className="form-control"
                pattern="[0-9]*"
                name="we"
                placeholder="Enter Months"
                value={this.state.we}
                onChange={this.onChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="toefl">TOEFL/IELTS Score</label>
              <input
                type="number" 
                className="form-control"
                pattern="[0-9]*"
                name="toefl"
                placeholder="Enter Score"
                value={this.state.toefl}
                onChange={this.onChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="lor1">Designation of your LOR Provider 1</label>
              <input
                type="text"
                className="form-control"
                name="first_name"
                placeholder="Enter your first name"
                value={this.state.first_name}
                onChange={this.onChange}
              />
          </div>
          <div className="form-group">
              <label htmlFor="lor2">Designation of your LOR Provider 2</label>
              <input
                type="text"
                className="form-control"
                name="first_name"
                placeholder="Enter your first name"
                value={this.state.first_name}
                onChange={this.onChange}
              />
          </div>
          <div className="form-group">
              <label htmlFor="lor3">Designation of your LOR Provider 3</label>
              <input
                type="text"
                className="form-control"
                name="first_name"
                placeholder="Enter your first name"
                value={this.state.first_name}
                onChange={this.onChange}
              />
          </div>
          <div className="form-group">
              <label htmlFor="lor4">Designation of your LOR Provider 4</label>
              <input
                type="text"
                className="form-control"
                name="first_name"
                placeholder="Enter your first name"
                value={this.state.first_name}
                onChange={this.onChange}
              />
          </div>
            <div className="form-group">
              <label htmlFor="resume">Uploaded Resume</label>
              <br></br>
              <button type="button" onClick={this.Previewresume}>
                  Preview Resume
              </button>
            </div>
            <div className="form-group">
              <label htmlFor="sop">Uploaded SOP</label>
              <br></br>
              <button type="button" onClick={this.Previewsop}>
                  Preview Resume
              </button>
            </div>
          </form>
      </div>

    );
  }
}

const authCondition = authUser =>
  !!authUser && authUser.providerData[0].providerId !== "facebook.com"; //true and false

export default withAuthorization(authCondition)(AccountPage);
