import React, { Component } from "react";
import { db } from "../firebase";
import withAuthorization from "./withAuthorization";
import axios from 'axios';

class Predict extends Component {

  state = {
    users: null,
    username: '',
    loading: true,
    first_name: '',
    last_name: '',
    email: '',
    grade: '',
    update: 0,
    gre: '',
    we: '',
    toefl:'',
    resume: '',
    sop: '',
    entry: '',
    uid: '',
    redirect: null,
    lor1: '',
    lor2: '',
    lor3: '',
    lor4: '',
    progresssop: '',
    progressresume: '',
    url_resume: '',
    url_sop: '',
    json: ''
  };


  componentDidMount() {

    const { loggedUser } = this.props;
    if(loggedUser)
    {
    db.doGetAnUnser(loggedUser.uid).then(res => {
      this.setState({
        username: res.val().username,
        email: res.val().email,
        loading: false,
        uid: loggedUser.uid
      });
      console.log(res.val())

    });
    }

    db.doGetForm(loggedUser.uid).then(res => {
      this.setState({
        first_name: res.val().first_name,
        last_name: res.val().last_name,
        grade: res.val().grade,
        gre: res.val().gre,
        we: res.val().work_ex,
        toefl: res.val().toefl,
        lor1: res.val().lor1,
        lor2: res.val().lor2,
        lor3: res.val().lor3,
        lor4: res.val().lor4
      });
      console.log(res.val())
    });
    axios.post('https://21e3096a.ngrok.io/', {
            'email': (this.state.email).toString(),
            'grade': this.state.grade,
            'gre': this.state.gre,
            'work_ex': this.state.we,
            'toefl': this.state.toefl,
            'uid': (this.state.uid).toString(),
            'lor1': this.state.lor1,
            'lor2': this.state.lor2,
            'lor3': this.state.lor3,
            'lor4': this.state.lor4
          })
          .then((response) => {
            console.log(response);
            this.setState({
              json: response
            })
          }, (error) => {
            console.log(error);
          });
  }

  render() {
    return (
      <div>
        <h1>Under Construction</h1>
        <div>
    <h4>Thank you for your patience {this.state.first_name}</h4>
        </div>
        </div>
    );
  }
}

const authCondition = authUser => !!authUser;

export default withAuthorization(authCondition)(Predict); //grants authorization to open endpoint if an user is signed in
// export default Predict;
